`describe('CAMNavPanelListItem with isRedirect false plus highlight li', () => {
    let wrapper;
    let a;

    const navData = {
        title: 'My Orders',
        isRedirect: false,
        isExternalLink: false,
        url: '/orders',
    };

    const location = {
        pathname: '/orders',
        basename: '',
    };


    beforeEach(() => {
        a = sinon.stub(document.body, 'getElementsByClassName');
        const li = {
            getElementsByTagName: sinon.stub(),
        };
        const eleme = a.withArgs('CAMNavPanel-rewardsMenu').returns([li]);
        console.log('Elements --------', eleme.getCall(0));

        wrapper = shallow(
            <CAMNavPanelListItem
                navData={navData}
                location={location}
                />,
        );
        wrapper.setState({ currentView: navData.url });
    });

    it('should render CAMNavPanelListItem with Link as well', () => {
        expect(wrapper.find('li')).to.have.length(1);
        expect(wrapper.find('li').hasClass('active')).to.equal(true);
        expect(wrapper.find('Link')).to.have.length(1);
    });

    it('should click and activate activeClass', () => {
        wrapper.find('Link').simulate('click', { button: 0 });
    });

    afterEach(() => {
        wrapper.unmount();
        a.restore();
    });
});`


