```js
import React from 'react';

const a = [1, 2]
const b = [3, 4]
const arr = [1, 2, 3, 4, 5, 6, 7, 8, 9]

class Checky extends React.Component {
  render() {
    return (
      <input
        type="checkbox"
        //  checked={this.props.checked}
        defaultChecked={this.props.checked}
        onChange={(event) => {
          this.setState({
            isChecked: !event.target.checked
          })
        }}
      />
    );
  }
}

class Home extends React.Component {
  state = {
    arr: a
  }

  render() {
    return (
      <div>
        <a href="#" onClick={() => {
          this.setState({ arr: this.state.arr === a ? b : a });
        }}>Toggle</a>
        <hr />
        {arr.map((item, index) => {
          return (
            <Checky key={index} checked={this.state.arr.indexOf(index) > -1} />
          )
        })}
      </div>
    );
  }
}
export default Home;
```

