//File.js
const ListMaker = props => {
    let names = ['yousef', 'ahemd', 'mohamed']
    return (
        <div>
            <div map={names}></div>
        </div>
    )
}

const App = () => {
    return (
        <ListMaker />
    )
}


ReactDOM.render(<App/>, document.querySelector('#app'))
