var RegexTextField = React.createClass({
  handleChange: function (e) {
    var patt = this.props.regex;                   // get regex obj
    var val = e.target.value;
    console.log('----------');
    console.log('patt:' + patt + ' value:' + val);
    console.log('result:' + patt.test(val));
