use Regexp

```
var RegexTextField = React.createClass({
  handleChange: function (e) {
    var patt = RegExp(this.props.regex, 'g');        // use RegExp
    var val = e.target.value;
    console.log('----------');
    console.log('patt:' + patt + ' value:' + val);
    console.log('result:' + patt.test(val));
  },
  render: function() {
    return (
        <input type="text" regex={this.props.regex} onChange={this.handleChange} />
    );
  }
});

React.render(
  <RegexTextField regex="[\d]" />,                   // use string
  document.getElementById('regex-text-field')
);

// all match ok 
