  ]
},

_onChange(id) {
    let items = this.state.items;
    items.every((item, index) => {
       if(item.id === id) {
           item.checked = ! item.checked
           return false; 
       }
       return true;
    })
   this.setState({
       items: items
   })
},

render() {
  checkboxList = this.state.items.map((item, index) => {
       return( <div key={index}>
                       <input type="checkbox checked={item.checked} onChange={this._onChange.bind(this, item.id)} />
                 </div> )
  })
  return (
      <section className={this.state.showModal ? "modal" : "modal hide"}>
         {checkboxList}
       </section>
  )

}

