import React from 'react';
import ReactDOM from 'react-dom';

class QuickBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            IsSetFontToggled: false,
            IsFindFormToggled: false,
            value: 'To find, type and hit enter'
        };
        this.ToggleFind = this.ToggleFind.bind(this);
    }

    ToggleFind() {
        if(this.state.IsFindFormToggled) {
            this.setState({
                IsFindFormToggled: false
            });
        } else {
            this.setState({
                IsSetFontToggled: false,
                IsFindFormToggled: true
            });
        }
    }
    FindString(str) {
        if (parseInt(navigator.appVersion) < 4) return;
        var strFound;
        if (window.find) {

            // CODE FOR BROWSERS THAT SUPPORT window.find

            strFound = self.find(str);
            if (!strFound) {
                strFound = self.find(str, 0, 1);
                while (self.find(str, 0, 1)) continue;
            }
        } else if (navigator.appName.indexOf("Microsoft") != -1) {

            // EXPLORER-SPECIFIC CODE

            if (TRange != null) {
                TRange.collapse(false);
                strFound = TRange.findText(str);
                if (strFound) TRange.select();
            }
            if (TRange == null || strFound == 0) {
                TRange = self.document.body.createTextRange();
                strFound = TRange.findText(str);
                if (strFound) TRange.select();
            }
        } else if (navigator.appName == "Opera") {
            alert("Opera browsers not supported, sorry...")
            return;
        }
        if (!strFound) alert("String '" + str + "' not found!")
        return;
        console.log(window.getSelection());
    }
    FindChange(event) {
        this.setState({
            value: event.target.value
        });
    }
    render() {
        return (
            <div>
                <div className="quickBar-child">
                    <button className="Find" onClick={this.ToggleFind} data-active={this.state.IsFindFormToggled} title="Find">
                        <ICON src={'../build/assets/images/find.svg'}/>
                    </button>
                    <div className={this.state.IsFindFormToggled ? "FindForm Active" : "FindForm unActive"}>
                        <input type="text" id="Search_Input" value={this.state.value} onChange={this.FindChange} onKeyPress={this.FindString(this.state.value)}/>
                    </div>
                </div>
            </div>
        );
    }
}

ReactDOM.render(<QuickBar/>, document.getElementById('header-quickbar'));
